sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var pathParms = request.pathParams;
    var user = pathParms.user_name;

    var grUsr = new GlideRecord('sys_user');
    grUsr.get('sys_id', user);

    var grCnt = new GlideAggregate('x_58872_needit_needit');
    grCnt.addAggregate('COUNT');
    grCnt.addQuery('u_requested_for', user);
    grCnt.groupBy('u_request_type');
    grCnt.query();

    var total = 0;
    var count = 0;
    var usrRecs = [];
    while (grCnt.next()) {
        count = grCnt.getAggregate('COUNT');
        var reqType = grCnt.u_request_type.getDisplayValue();
        usrRecs.push({
            'request type': reqType,
            'Count': count
        });
        total = parseInt(count) + parseInt(grCnt.getAggregate('COUNT'));
    }

    var out = {};
    out.number = count;
    out.usrRecs = usrRecs;
    out.total = total;
    out.user = {
        'user name': grUsr.user_name,
        'sys_id': grUsr.sys_id
    };
    response.setBody(out);


})(request, response);
